{
  "name": "Updated Series Name",
  "start_date": "2024-03-22T00:00:00Z",
  "end_date": "2024-03-25T23:59:59Z"
}

{
  "team_a_player_count": 10,
  "team_b_player_count": 10,
  "total_overs": 15,
  "batting_team": "B"
}

{
    "match_id": "{{match_id}}"
}

{
    "query": "query GetLiveScorecard($matchId: String!) {
        liveScorecard(match_id: $matchId) {
            match_id
            current_score {
                runs
                wickets
                overs
                run_rate
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetCurrentScore($matchId: String!) {
        liveScorecard(match_id: $matchId) {
            match_id
            current_score {
                runs
                wickets
                overs
                run_rate
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetCurrentOver($matchId: String!) {
        liveScorecard(match_id: $matchId) {
            match_id
            current_over {
                over_number
                total_runs
                total_balls
                total_wickets
                status
                balls {
                    ball_number
                    ball_type
                    run_type
                    runs
                    is_wicket
                }
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetMatchSummary($matchId: String!) {
        liveScorecard(match_id: $matchId) {
            match_id
            match_number
            series_name
            team_a
            team_b
            total_overs
            toss_winner
            current_innings
            match_status
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetInningsSummary($matchId: String!) {
        liveScorecard(match_id: $matchId) {
            match_id
            innings {
                innings_number
                batting_team
                total_runs
                total_wickets
                total_overs
                status
                extras {
                    total
                }
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetMatchDetails($matchId: String!) {
        matchDetails(match_id: $matchId) {
            match_id
            match_number
            series_name
            team_a
            team_b
            total_overs
            toss_winner
            toss_type
            current_innings
            match_status
            batting_team
            team_a_player_count
            team_b_player_count
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetMatchStatistics($matchId: String!) {
        matchStatistics(match_id: $matchId) {
            total_runs
            total_wickets
            total_overs
            total_balls
            run_rate
            extras {
                byes
                leg_byes
                wides
                no_balls
                total
            }
            innings_count
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetInningsDetails($matchId: String!, $inningsNumber: Int!) {
        inningsDetails(match_id: $matchId, innings_number: $inningsNumber) {
            innings_number
            batting_team
            total_runs
            total_wickets
            total_overs
            total_balls
            status
            extras {
                byes
                leg_byes
                wides
                no_balls
                total
            }
            overs {
                over_number
                total_runs
                total_balls
                total_wickets
                status
                balls {
                    ball_number
                    ball_type
                    run_type
                    runs
                    byes
                    is_wicket
                    wicket_type
                }
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}",
        "inningsNumber": 1
    }
}

{
    "query": "query GetOverDetails($matchId: String!, $inningsNumber: Int!, $overNumber: Int!) {
        overDetails(match_id: $matchId, innings_number: $inningsNumber, over_number: $overNumber) {
            over_number
            total_runs
            total_balls
            total_wickets
            status
            balls {
                ball_number
                ball_type
                run_type
                runs
                byes
                is_wicket
                wicket_type
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}",
        "inningsNumber": 1,
        "overNumber": 1
    }
}

{
    "query": "query GetBallDetails($matchId: String!, $inningsNumber: Int!, $overNumber: Int!) {
        ballDetails(match_id: $matchId, innings_number: $inningsNumber, over_number: $overNumber) {
            ball_number
            ball_type
            run_type
            runs
            byes
            is_wicket
            wicket_type
        }
    }",
    "variables": {
        "matchId": "{{match_id}}",
        "inningsNumber": 1,
        "overNumber": 1
    }
}

{
    "query": "query GetMatchTeams($matchId: String!) {
        matchTeams(match_id: $matchId) {
            id
            name
            players_count
            created_at
            updated_at
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetMatchPlayers($matchId: String!) {
        matchPlayers(match_id: $matchId) {
            id
            name
            team_id
            created_at
            updated_at
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetPlayerStatistics($matchId: String!) {
        playerStatistics(match_id: $matchId) {
            player_id
            player_name
            team_id
            runs_scored
            balls_faced
            wickets_taken
            overs_bowled
            runs_conceded
            strike_rate
            economy_rate
        }
    }",
    "variables": {
        "matchId": "{{match_id}}"
    }
}

{
    "query": "query GetLatestOver($matchId: String!, $inningsNumber: Int!) {
        latestOver(match_id: $matchId, innings_number: $inningsNumber) {
            over_number
            total_runs
            total_balls
            total_wickets
            status
            balls {
                ball_number
                ball_type
                run_type
                runs
                byes
                is_wicket
                wicket_type
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}",
        "inningsNumber": 1
    }
}

{
    "query": "query GetAllOvers($matchId: String!, $inningsNumber: Int!) {
        allOvers(match_id: $matchId, innings_number: $inningsNumber) {
            over_number
            total_runs
            total_balls
            total_wickets
            status
            balls {
                ball_number
                ball_type
                run_type
                runs
                byes
                is_wicket
                wicket_type
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}",
        "inningsNumber": 1
    }
}

{
    "query": "query GetInningsScore($matchId: String!, $inningsNumber: Int!) {
        inningsScore(match_id: $matchId, innings_number: $inningsNumber) {
            innings_number
            batting_team
            total_runs
            total_wickets
            total_overs
            total_balls
            status
            extras {
                byes
                leg_byes
                wides
                no_balls
                total
            }
        }
    }",
    "variables": {
        "matchId": "{{match_id}}",
        "inningsNumber": 1
    }
}
